let movieNameRef=document.getElementById("movie-name"),searchBtn=document.getElementById("search-btn"),result=document.getElementById("result");key="cf6c42d4";let getMovie=()=>{let e=movieNameRef.value,n=`http://www.omdbapi.com/?apikey=${key}&t=${e}`;e.length<=0?result.innerHTML="<h3 class='msg'>Please enter</h3>":fetch(n).then((e=>e.json())).then((e=>{"True"==e.Response?result.innerHTML=`\n                  <div class="info">\n                      <img src=${e.Poster} class="poster">\n                      <div>\n                          <h2>${e.Title}</h2>\n                          <div class="genre">\n                              <div>${e.Genre.split(",").join("</div><div>")}</div>\n                          </div>\n                      </div>\n                  </div>\n              `:result.innerHTML=`<h3 class='msg'>${e.Error}</h3>`})).catch((()=>{result.innerHTML="<h3 class='msg'>Error Occured</h3>"}))};searchBtn.addEventListener("click",getMovie),window.addEventListener("load",getMovie);