let movieNameRef=document.getElementById("movie-name"),searchBtn=document.getElementById("search-btn"),result=document.getElementById("result");key="cf6c42d4";let getMovie=()=>{let e=movieNameRef.value,t=`http://www.omdbapi.com/?apikey=${key}&s=${e}&page=2`;e.length<=0?result.innerHTML="<h3 class='msg'>Please enter</h3>":fetch(t).then((e=>e.json())).then((e=>{console.log(e),innerHTML(e),"True"==e.resp?result.innerHTML+=`\n                <h2>${e.Title}</h2>`:result.innerHTML=`<h3 class='msg'>${e.Error}</h3>`})).catch((()=>{result.innerHTML="<h3 class='msg'>Error Occured</h3>"}))};searchBtn.addEventListener("click",getMovie),window.addEventListener("load",getMovie);